function changeLanguage(lang) {
    localStorage.setItem('selectedLanguage', lang); // –¢–∞“£–¥–∞–ª“ì–∞–Ω —Ç—ñ–ª–¥—ñ —Å–∞“õ—Ç–∞—É
    document.querySelector('#current-lang').innerHTML = `
      <img src="flags/${lang === 'kk' ? 'KZ-1x1.5.png' : lang === 'ru' ? 'Flag_of_Russia.png' : 'Flag_of_the_United_Kingdom_(1-2).svg.png'}" class="flag"> 
      ${lang.toUpperCase()} ‚ñº`;
    
    document.querySelectorAll('[data-translate]').forEach(el => {
      const key = el.getAttribute('data-translate');
      if (translations[lang] && translations[lang][key]) {
        el.textContent = translations[lang][key]; // –°—Ç–∏–ª—å ”©–∑–≥–µ—Ä–º–µ—É—ñ “Ø—à—ñ–Ω textContent “õ–æ–ª–¥–∞–Ω–∞–º—ã–∑
      }
    });
}

// –ë–µ—Ç—Ç—ñ –∂“Ø–∫—Ç–µ–≥–µ–Ω–¥–µ —Å–∞“õ—Ç–∞–ª“ì–∞–Ω —Ç—ñ–ª–¥—ñ “õ–æ–ª–¥–∞–Ω—É
document.addEventListener("DOMContentLoaded", () => {
    const savedLang = localStorage.getItem('selectedLanguage') || 'en';
    changeLanguage(savedLang);
});